    public function showPDF($myhtml)
    {
        // create new PDF document
        $pdf = new TCPDF(PDF_PAGE_ORIENTATION, PDF_UNIT, PDF_PAGE_FORMAT, true, 'UTF-8', false);

        // set document information
        $pdf->SetCreator(PDF_CREATOR);
        $pdf->SetAuthor('Portal');
        $pdf->SetTitle('Invoice (Purchase Order)');
        $pdf->SetSubject('Purchase Order Invoice');
        $pdf->SetKeywords('Purchase, Purchase Order');
        
        // set font
        $pdf->SetFont('dejavusans', '', 11);

        // set auto page breaks
        $pdf->SetAutoPageBreak(TRUE, PDF_MARGIN_BOTTOM);

        // set image scale factor
        $pdf->setImageScale(PDF_IMAGE_SCALE_RATIO);

        // add a page
        $pdf->AddPage();

        $imageData = base64_decode('');
        
        $pdf->Image('@'.$imageData, 9, 11);
        
        // output the HTML content
        $pdf->writeHTML($myhtml, true, false, true, false, '');

        // reset pointer to the last page
        $pdf->lastPage();

        // ---------------------------------------------------------

        //Close and output PDF document
        $pdf->Output('purchase_order.pdf', 'I');
    }
